const menuHiddenData=document.querySelector(".menu-hidden__container"),menuBar=document.querySelector("#menu-bar");menuBar.addEventListener("click",()=>{menuHiddenData.classList.contains("hidden")?(menuHiddenData.classList.remove("hidden"),menuBar.setAttribute("src","img/close.png")):(menuHiddenData.classList.add("hidden"),menuBar.setAttribute("src","img/bar.png"))});